$(document).ready(function(){window.onbeforeunload=function(){window.scrollTo(0,0)},$("input[name='colors']").on("click",function(){var e=$(this).val();localStorage.setItem("color",e)}),$("input[name='genders']").on("click",function(){var e=$(this).val();localStorage.setItem("gender",e)}),$("input[name='sizes']").on("click",function(){var e=$(this).val();localStorage.setItem("size",e)}),$("#addToCard").on("click",function(){if(null!==localStorage.getItem("color")&&null!==localStorage.getItem("gender")&&null!==localStorage.getItem("size")){var e=localStorage.getItem("color"),o=localStorage.getItem("gender"),l=localStorage.getItem("size"),t=JSON.stringify({color:e,gender:o,size:l});localStorage.clear(),console.log(t)}else alert("Please, choose all parameters! ")})});